\"&gt;&nbsp;--&gt;<br />&lt;!--&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;property&nbsp;name=\"JDBC.Driver\"&nbsp;value=\"jdbc:apache:commons:dbcp:/pool\"/&gt;&nbsp;--&gt;<br />&lt;!--&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;/dataSource&gt;&nbsp;--&gt;<br /><br />&lt;!--&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;dataSource&nbsp;type=\"SIMPLE\"&gt;&nbsp;--&gt;<br />&lt;!--&nbsp;&nbsp;&lt;property&nbsp;name=\"JDBC.Driver\"&nbsp;value=\"com.mysql.jdbc.Driver\"/&gt;&nbsp;--&gt;<br />&lt;!--&nbsp;&nbsp;&lt;property&nbsp;name=\"JDBC.ConnectionURL\"&nbsp;value=\"jdbc:mysql://192.168.0.110:3306/CUREnCARE?useUnicode=true&amp;characterEncoding=utf8\"/&gt;&nbsp;--&gt;<br />&lt;!--&nbsp;&nbsp;&lt;property&nbsp;name=\"JDBC.Username\"&nbsp;value=\"curencare\"/&gt;&nbsp;--&gt;<br />&lt;!--&nbsp;&nbsp;&lt;property&nbsp;name=\"JDBC.Password\"&nbsp;value=\"Redbutton1\"/&gt;&nbsp;&nbsp;--&gt;<br />&lt;!--&nbsp;&nbsp;&lt;/dataSource&gt;&nbsp;--&gt;<br />&lt;/transactionManager&gt;<br /><br />&lt;sqlMap&nbsp;resource=\"ibatis/configuration/Board.xml\"/&gt;<br />&nbsp;&nbsp;&lt;sqlMap&nbsp;resource=\"ibatis/configuration/Etc.xml\"/&gt;<br />&nbsp;&nbsp;&lt;sqlMap&nbsp;resource=\"ibatis/configuration/Admin.xml\"/&gt;<br />&nbsp;&nbsp;&lt;sqlMap&nbsp;resource=\"ibatis/configuration/User.xml\"/&gt;<br />&lt;/sqlMapConfig&gt;</div>\r\n<p>참고 사이트 : <a href=\"http://samples.tistory.com/138\">http://samples.tistory.com/138</a></p>\r\n</p>\r\n\r\n<p></p>\r\n\r\n<p></p>',2,0,0,0,0,5,NULL,'t157','157','수진',158,'sujin.im@devmon.co.kr','','','N;','20130131104919','20130131110502',NULL,'1.222.43.250',-582,-589,'Y','N','PUBLIC','ALLOW'),(591,106,641,'ko','N','LayerPopup','N','N','<p>1. &lt;div class=\"mask\"&gt;&lt;/div&gt;</p>\r\n\r\n<p>--&gt; 이걸 잘보이는 곳에 놓는다</p>\r\n\r\n<p>layerpopup을 띄웠을경우 mask의 용도는 layerpopup외의 것을 클릭했을 때의 동작을 지정하기에 유용</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span class=\"id\">#mask</span> <span class=\"rules\">{&nbsp; <br />&nbsp; <span class=\"rule\"><span class=\"attribute\">position</span></span></span>:<span class=\"value\">absolute;</span>&nbsp; <br />&nbsp; <span class=\"rule\"><span class=\"attribute\">z-index</span></span>:<span class=\"value\"><span class=\"number\">9000</span></span>;&nbsp; <br />&nbsp; <span class=\"rule\"><span class=\"attribute\">background-color</span></span>:<span class=\"value\"><span class=\"hexcolor\">#000</span></span>;&nbsp; <br />&nbsp; <span class=\"rule\"><span class=\"attribute\">display</span></span>:<span class=\"value\">none;</span>&nbsp; <br />&nbsp; <span class=\"rule\"><span class=\"attribute\">left</span></span>:<span class=\"value\"><span class=\"number\">0</span></span>;<br />&nbsp; <span class=\"rule\"><span class=\"attribute\">top</span></span>:<span class=\"value\"><span class=\"number\">0</span></span>;<br />&nbsp;} <br /><code class=\"ruby\">function wrapWindowByMask(){<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span class=\"regexp\">//</span>화면의 높이와 너비를 구한다.<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; var maskHeight = <span class=\"variable\">$(</span>document).height();&nbsp; <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; var maskWidth = <span class=\"variable\">$(</span>window).width();&nbsp; <br /><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span class=\"regexp\">//</span>마스크의 높이와 너비를 화면 것으로 만들어 전체 화면을 채운다.<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span class=\"variable\">$(</span><span class=\"string\">\'#mask\'</span>).css({<span class=\"string\">\'width\'</span><span class=\"symbol\">:maskWidth</span>,<span class=\"string\">\'height\'</span><span class=\"symbol\">:maskHeight</span>});&nbsp; <br /><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span class=\"regexp\">//</span>애니메이션 효과<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span class=\"variable\">$(</span><span class=\"string\">\'#mask\'</span>).fadeIn(<span class=\"number\">1000</span>);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span class=\"variable\">$(</span><span class=\"string\">\'#mask\'</span>).fadeTo(<span class=\"string\">\"slow\"</span>,<span class=\"number\">0</span>.<span class=\"number\">8</span>);&nbsp;&nbsp;&nbsp; <br />}</code></p>\r\n\r\n<p><code class=\"ruby\">2.</code></p>\r\n\r\n<p><code class=\"ruby\">modal window의 닫기 버튼 클릭시</code></p>\r\n\r\n<p><code class=\"ruby\">$(<span class=\"string\">\'.window .close\'</span>).click(<span class=\"function\"><span class=\"keyword\">function</span></span> <span class=\"params\">(e)</span> {&nbsp; <br />&nbsp;&nbsp;&nbsp; <span class=\"comment\">//링크 기본동작은 작동하지 않도록 한다.</span><br />&nbsp;&nbsp;&nbsp; e.preventDefault();&nbsp; <br />&nbsp;&nbsp;&nbsp; $(<span class=\"string\">\'#mask, .window\'</span>).hide();&nbsp; <br />});</code></p>\r\n\r\n<p><code class=\"ruby\"></code><code class=\"ruby\"></code>&nbsp;</p>\r\n\r\n<p><code class=\"ruby\"><span class=\"comment\">//검은 막을 눌렀을 때</span><br />$(<span class=\"string\">\'#mask\'</span>).click(<span class=\"function\"><span class=\"keyword\">function</span></span> <span class=\"params\">()</span> {&nbsp; <br />&nbsp;&nbsp;&nbsp; $(<span class=\"keyword\">this</span>).hide();&nbsp; <br />&nbsp;&nbsp;&nbsp; $(<span class=\"string\">\'.window\'</span>).hide();&nbsp; <br />});</code></p>\r\n\r\n<p><code class=\"ruby\">--&gt; 검은 막을 눌렀을때 띄운 modal을 닫히게 하기 싫다면 해당하는 코드는 생략 \r\n</code><div title=\"layerPopup.jsp\" style=\"background: url(&quot;http://www.devmon.co.kr/xe/modules/editor/components/code_highlighter/code.png&quot;) no-repeat right top rgb(250, 250, 250); border-width: 1px 1px 1px 5px; border-style: dotted dotted dotted solid; border-color: rgb(102, 102, 102) rgb(102, 102, 102) rgb(102, 102, 102) rgb(34, 170, 238); padding: 5px;\" nogutter=\"false\" collapse=\"true\" first_line=\"1\" code_type=\"Xml\" editor_component=\"code_highlighter\" highlight=\"\"><code class=\"ruby\">&lt;%@&nbsp;page&nbsp;language=\"java\"&nbsp;contentType=\"text/html;&nbsp;charset=UTF-8\"<br />&nbsp;&nbsp;&nbsp;&nbsp;pageEncoding=\"UTF-8\"%&gt;<br />&lt;!DOCTYPE&nbsp;html&nbsp;PUBLIC&nbsp;\"-//W3C//DTD&nbsp;HTML&nbsp;4.01&nbsp;Transitional//EN\"&nbsp;\"http://www.w3.org/TR/html4/loose.dtd\"&gt;<br />&lt;html&gt;<br />&lt;head&gt;<br />&lt;meta&nbsp;http-equiv=\"Content-Type\"&nbsp;content=\"text/html;&nbsp;charset=UTF-8\"&gt;<br />&lt;style&nbsp;type=\"text/css\"&gt;<br />#mask&nbsp;{&nbsp;&nbsp;<br />&nbsp;&nbsp;position:absolute;&nbsp;&nbsp;<br />&nbsp;&nbsp;z-index:9000;&nbsp;&nbsp;<br />&nbsp;&nbsp;background-color:#000;&nbsp;&nbsp;<br />&nbsp;&nbsp;display:none;&nbsp;&nbsp;<br />&nbsp;&nbsp;left:0;<br />&nbsp;&nbsp;top:0;<br />}&nbsp;<br />.window{<br />&nbsp;&nbsp;display:&nbsp;none;<br />&nbsp;&nbsp;position:absolute;&nbsp;&nbsp;<br />&nbsp;&nbsp;left:100px;<br />&nbsp;&nbsp;top:100px;<br />&nbsp;&nbsp;z-index:10000;<br /><br />&nbsp;&nbsp;background-color:#ffffff;<br />&nbsp;&nbsp;width:600px;<br />&nbsp;&nbsp;height:200px;<br />&nbsp;&nbsp;position:absolute;<br />&nbsp;&nbsp;display:none;<br />}<br /><br />&nbsp;&nbsp;<br />&nbsp;&lt;/style&gt;<br />&lt;script&nbsp;src=\"/js/jquery.js\"&gt;&lt;/script&gt;<br />&nbsp;&lt;script&nbsp;type=\"text/javascript\"&gt;<br />function&nbsp;wrapWindowByMask(){<br />//화면의&nbsp;높이와&nbsp;너비를&nbsp;구한다.<br />var&nbsp;maskHeight&nbsp;=&nbsp;$(document).height();&nbsp;&nbsp;<br />var&nbsp;maskWidth&nbsp;=&nbsp;$(window).width();&nbsp;&nbsp;<br /><br />//마스크의&nbsp;높이와&nbsp;너비를&nbsp;화면&nbsp;것으로&nbsp;만들어&nbsp;전체&nbsp;화면을&nbsp;채운다.<br />$(\'#mask\').css({\'width\':maskWidth,\'height\':maskHeight});&nbsp;&nbsp;<br /><br />//애니메이션&nbsp;효과&nbsp;-&nbsp;일단&nbsp;1초동안&nbsp;까맣게&nbsp;됐다가&nbsp;80%&nbsp;불투명도로&nbsp;간다.<br />$(\'#mask\').fadeIn(1000);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br />$(\'#mask\').fadeTo(\"slow\",0.8);&nbsp;&nbsp;&nbsp;&nbsp;<br /><br />//윈도우&nbsp;같은&nbsp;거&nbsp;띄운다.<br />$(\'.window\').show();<br />}<br /><br />$(document).ready(function(){<br />//검은&nbsp;막&nbsp;띄우기<br />$(\'.openMask\').click(function(e){<br />e.preventDefault();<br />wrapWindowByMask();<br />})