&nbsp;margin:&nbsp;0&nbsp;0&nbsp;20px&nbsp;0;&nbsp;overflow-x:&nbsp;scroll;&nbsp;}<br />\r\n		h1&nbsp;{&nbsp;font:&nbsp;bold&nbsp;50px/1&nbsp;Sans-Serif;&nbsp;letter-spacing:&nbsp;-2px;&nbsp;margin:&nbsp;0&nbsp;0&nbsp;20px&nbsp;0;&nbsp;}<br />\r\n<br />\r\n		body&nbsp;{&nbsp;width:&nbsp;50%;&nbsp;margin:&nbsp;50px&nbsp;auto;&nbsp;padding:&nbsp;20px;&nbsp;background:&nbsp;white;&nbsp;}<br />\r\n		iframe&nbsp;{&nbsp;width:&nbsp;100%;&nbsp;margin:&nbsp;0&nbsp;0&nbsp;20px&nbsp;0;&nbsp;}<br />\r\n		<br />\r\n		@media&nbsp;(max-width:&nbsp;480px)&nbsp;{<br />\r\n			body&nbsp;{&nbsp;width:&nbsp;90%;&nbsp;}<br />\r\n		}<br />\r\n		<br />\r\n	&lt;/style&gt;<br />\r\n	<br />\r\n	&lt;script&nbsp;src=\"http://ajax.googleapis.com/ajax/libs/jquery/1.6.1/jquery.min.js\"&gt;&lt;/script&gt;<br />\r\n	<br />\r\n&lt;/head&gt;<br />\r\n<br />\r\n&lt;body&gt;<br />\r\n<br />\r\n	&lt;h1&gt;Fluid&nbsp;Width&nbsp;YouTube&nbsp;Videos&lt;br&gt;<br />\r\n	&lt;small&gt;of&nbsp;Different&nbsp;Aspect&nbsp;Ratios&lt;/small&gt;&lt;/h1&gt;<br />\r\n	<br />\r\n	&lt;iframe&nbsp;width=\"1280\"&nbsp;height=\"750\"&nbsp;src=\"http://www.youtube.com/embed/MmpAXq7sq8s?rel=0&amp;hd=1\"&nbsp;frameborder=\"0\"&nbsp;allowfullscreen&gt;&lt;/iframe&gt;<br />\r\n	<br />\r\n	&lt;iframe&nbsp;width=\"960\"&nbsp;height=\"750\"&nbsp;src=\"http://www.youtube.com/embed/8neXfLo2f3o?rel=0&amp;hd=1\"&nbsp;frameborder=\"0\"&nbsp;allowfullscreen&gt;&lt;/iframe&gt;<br />\r\n	<br />\r\n	&lt;iframe&nbsp;width=\"300\"&nbsp;height=\"420\"&nbsp;src=\"http://www.youtube.com/embed/Odo0RcqBVDc?rel=0\"&nbsp;frameborder=\"0\"&nbsp;allowfullscreen&gt;&lt;/iframe&gt;<br />\r\n	<br />\r\n	&lt;h1&gt;jQuery&nbsp;Script&lt;/h1&gt;<br />\r\n	<br />\r\n	&lt;script&gt;//&nbsp;By&nbsp;Chris&nbsp;Coyier&nbsp;&amp;&nbsp;tweaked&nbsp;by&nbsp;Mathias&nbsp;Bynens<br />\r\n<br />\r\n$(function()&nbsp;{<br />\r\n<br />\r\n	//&nbsp;Find&nbsp;all&nbsp;YouTube&nbsp;videos<br />\r\n	var&nbsp;$allVideos&nbsp;=&nbsp;$(\"iframe[src^=\'http://www.youtube.com\']\"),<br />\r\n<br />\r\n	&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;The&nbsp;element&nbsp;that&nbsp;is&nbsp;fluid&nbsp;width<br />\r\n	&nbsp;&nbsp;&nbsp;&nbsp;$fluidEl&nbsp;=&nbsp;$(\"body\");<br />\r\n<br />\r\n	//&nbsp;Figure&nbsp;out&nbsp;and&nbsp;save&nbsp;aspect&nbsp;ratio&nbsp;for&nbsp;each&nbsp;video<br />\r\n	$allVideos.each(function()&nbsp;{<br />\r\n<br />\r\n		$(this)<br />\r\n			.data(\'aspectRatio\',&nbsp;this.height&nbsp;/&nbsp;this.width)<br />\r\n			<br />\r\n			//&nbsp;and&nbsp;remove&nbsp;the&nbsp;hard&nbsp;coded&nbsp;width/height<br />\r\n			.removeAttr(\'height\')<br />\r\n			.removeAttr(\'width\');<br />\r\n<br />\r\n	});<br />\r\n<br />\r\n	//&nbsp;When&nbsp;the&nbsp;window&nbsp;is&nbsp;resized<br />\r\n	//&nbsp;(You\'ll&nbsp;probably&nbsp;want&nbsp;to&nbsp;debounce&nbsp;this)<br />\r\n	$(window).resize(function()&nbsp;{<br />\r\n<br />\r\n		var&nbsp;newWidth&nbsp;=&nbsp;$fluidEl.width();<br />\r\n		<br />\r\n		//&nbsp;Resize&nbsp;all&nbsp;videos&nbsp;according&nbsp;to&nbsp;their&nbsp;own&nbsp;aspect&nbsp;ratio<br />\r\n		$allVideos.each(function()&nbsp;{<br />\r\n<br />\r\n			var&nbsp;$el&nbsp;=&nbsp;$(this);<br />\r\n			$el<br />\r\n				.width(newWidth)<br />\r\n				.height(newWidth&nbsp;*&nbsp;$el.data(\'aspectRatio\'));<br />\r\n<br />\r\n		});<br />\r\n<br />\r\n	//&nbsp;Kick&nbsp;off&nbsp;one&nbsp;resize&nbsp;to&nbsp;fix&nbsp;all&nbsp;videos&nbsp;on&nbsp;page&nbsp;load<br />\r\n	}).resize();<br />\r\n<br />\r\n});&lt;/script&gt;<br />\r\n<br />\r\n&lt;/body&gt;<br />\r\n<br />\r\n&lt;/html&gt;<br /></div><br /><p></p>\r\n<p><a href=\"https://www.google.com/url?sa=t&amp;rct=j&amp;q=&amp;esrc=s&amp;source=web&amp;cd=1&amp;ved=0CC8QFjAA&amp;url=http%3A%2F%2Fcss-tricks.com%2Ffluid-width-youtube-videos%2F&amp;ei=koRfUuqFBIqUiAerwIHACA&amp;usg=AFQjCNGBSlc4zf-dJl6jDdmuflbBYosCfw&amp;bvm=bv.54176721,d.aGc&amp;cad=rja\"></a></p>',0,0,0,0,0,1,NULL,'t140','140','shin',140,'shinhee.yi@devmon.co.kr','',NULL,'N;','20131017154223','20131017154223',NULL,'1.222.43